package uz.coder.mvpexample.adaptersimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.view.animation.AnimationUtilsimport androidx.recyclerview.widget.RecyclerViewimport com.squareup.picasso.Callbackimport com.squareup.picasso.Picassoimport uz.coder.mvpexample.Rimport uz.coder.mvpexample.databinding.BookItemBindingimport uz.coder.mvpexample.models.books.books.BookXimport java.lang.Exceptionimport java.util.*import kotlin.collections.ArrayListclass MyAdapter(private var arraylist: ArrayList<BookX>, var onClick: OnClick) :    RecyclerView.Adapter<MyAdapter.ViewH>() {    inner class ViewH(var binding: BookItemBinding) : RecyclerView.ViewHolder(binding.root) {        fun onBind(myClass: BookX) {            itemView.animation = AnimationUtils.loadAnimation(binding.root.context, R.anim.recy_anim)            Picasso.get().load(myClass.book_image).resize(200,200).into(binding.booImage, object :Callback{                override fun onSuccess() {                    binding.shimmerView.visibility = View.GONE                }                override fun onError(e: Exception?) {                    binding.booImage.setImageResource(R.drawable.warning)                }            })            binding.bookName.text = myClass.author            binding.booImage.setOnClickListener { onClick.click(myClass) }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewH {        return ViewH(BookItemBinding.inflate(LayoutInflater.from(parent.context), parent, false))    }    override fun getItemCount(): Int {        return arraylist.size    }    override fun onBindViewHolder(holder: ViewH, position: Int) {        holder.onBind(arraylist[position])    }    interface OnClick{        fun click(bookX: BookX)    }}