package uz.coder.mvpexample.adaptersimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.view.animation.AnimationUtilsimport androidx.recyclerview.widget.RecyclerViewimport com.squareup.picasso.Callbackimport com.squareup.picasso.Picassoimport uz.coder.mvpexample.Rimport uz.coder.mvpexample.databinding.BookItem2Bindingimport uz.coder.mvpexample.databinding.ItemWritersBindingimport uz.coder.mvpexample.models.books.books.BookXclass WritersAdapter(private val arraylist: ArrayList<String>) :    RecyclerView.Adapter<WritersAdapter.ViewH>() {    inner class ViewH(var binding: ItemWritersBinding) : RecyclerView.ViewHolder(binding.root) {        fun onBind(myClass: String) {            itemView.animation = AnimationUtils.loadAnimation(binding.root.context, R.anim.recy_anim)            Picasso.get().load(myClass).resize(200,200).into(binding.image)        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewH {        return ViewH(ItemWritersBinding.inflate(LayoutInflater.from(parent.context), parent, false))    }    override fun getItemCount(): Int {        return arraylist.size    }    override fun onBindViewHolder(holder: ViewH, position: Int) {        holder.onBind(arraylist[position])    }}