package uz.coder.mvpexample.mvpimport android.os.Handlerimport android.os.Looperclass Model : Contract.Model {    private val arrayList =        listOf(":)")    override fun getNextCourse(onFinishedListener: Contract.Model.OnFinishedListener) {        Handler(Looper.myLooper()!!).postDelayed({            onFinishedListener.onFinished(getRandomString)        }, 1000)    }    private val getRandomString:String    get() {        val random = java.util.Random()        val nextInt = random.nextInt(arrayList.size)        return arrayList[nextInt]    }}