package uz.coder.mvpexample.mvpclass Presenter(private var mainView: Contract.View?, private val model: Model) : Contract.Presenter,    Contract.Model.OnFinishedListener {    override fun onButtonClick() {        if (mainView != null) {            mainView!!.showProgress()        }        model.getNextCourse(this)    }    override fun onDestroy() {        mainView = null    }    override fun onFinished(s: String) {        if (mainView != null) {            mainView?.setString(s)            mainView?.hideProgress()        }    }}