package uz.coder.mvpexample.room.db.convertorsimport androidx.room.TypeConverterimport com.google.gson.Gsonimport com.google.gson.reflect.TypeTokenimport uz.coder.mvpexample.models.books.books.Isbnclass DataConvertor2 {    @TypeConverter    fun fromString(str: String): List<Isbn> {        val list = object : TypeToken<List<Isbn>>() {}.type        return Gson().fromJson(str, list)    }    @TypeConverter    fun fromList(list: List<Isbn>): String {        val gson = Gson()        return gson.toJson(list)    }}